INSERT INTO `university` VALUES (766, '普尔学院大学创办于1879年，坐落于大阪府,堺市，地理位置优越，环境优美。该校开设本科层次的学位教育，在校学生人数1122人,作为被中国教育部认可的大学...', '普尔学院大学', 5, NULL, 0, NULL, NULL, '1879', '大阪府,堺市', '072-292-7201', 'https://img.liuxue86.com/school/2017/0814/15027697618177.jpg', 'http://www.poole.ac.jp/');
